import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import { useQuery, gql } from '@apollo/client';

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <GetAuhtors/>
      </header>
    </div>
  );
}

const GET_AUTHORS = gql`
  {
    authors {
      name
      books
    }
  }
`;

const CHANGE_NAME = gql`
  mutation AuthorUpdate($id: Int!, $name: String!) {
    changeName(id: $id, name: $name) {
      name
    }
  }
`;


function GetAuhtors() {
  const { loading, error, data } = useQuery(GET_AUTHORS);


  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;



  return data.authors.map(({ name, books }) => (
      <AuthorEle name={name} books={books} />
  ));
}

function AuthorEle(props) {
    const {name, setName} = useState(props.name);

    const handleClick = () => {
      const { loading, error, data } = useQuery(CHANGE_NAME);

    }

    return (
    <div key={name}>
      <p>
        {name}: {books}
      </p>
      <input value={name} onChange={(event) => setName(event.target.value)} />
      <button onClick={handleClick} />
    </div>
    );

}

export default App;
